});"}, "id": "d9162ef4-48d6-49c6-9878-83d1132f3e6f"}, {"type": "content", "value": "<p data-block-key="2bahs"></p><hr/><p data-block-key="4l79u"></p>", "id": "e7d674b1-c31d-4fbf-a7e8-dd24bfeb88a3"}, {"type": "content", "value": "<h2 data-block-key="2bahs">Code complet</h2>", "id": "9a5f536f-bff5-43ad-819c-6a770ebfc068"}, {"type": "code_block", "value": {"language": "javascript", "code": "const canvas = document.getElementById('canvas1');
const ctx = canvas.getContext('2d');
canvas.width = window.innerWidth;
canvas.height = window.innerHeight;

let gardient = ctx.createLinearGradient(canvas.width/2, canvas.height/2, 100, canvas.height/2, canvas.width/2);
gardient.addColorStop(0, 'red');
gardient.addColorStop(0.6, 'cyan');
gardient.addColorStop(1, 'magenta');

class Symbol {
    constructor(x, y, fontSize, canvasHeight){
        this.characters = 'アァカサタナハマヤャラワガザダバパイィキシチニヒミリヰギジヂビピウゥクスツヌフムユュルグズブヅプエェケセテネヘメレヱゲゼデベペオォコソトホモヨョロヲゴゾドボポヴッン0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ';
        this.x = x;
        this.y = y;
        this.fontSize = fontSize;
        this.text = '';
        this.canvasHeight = canvasHeight;
    }
    draw(context){
        this.text = this.characters.charAt(Math.floor(Math.random()*this.characters.length));
        context.fillText(this.text, this.x * this.fontSize, this.y * this.fontSize);
        if (this.y * this.fontSize > this.canvasHeight && Math.random() > 0.98){
            this.y = 0;
        } else {
            this.y += 1;
        }
    }
}

class Effect {
    constructor(canvasWidth, canvasHeight){
        this.canvasWidth = canvasWidth;
        this.canvasHeight = canvasHeight;
        this.fontSize = 25;
        this.columns = this.canvasWidth/this.fontSize;
        this.symbols = [];
        this.#initialize();
        console.log(this.symbols);
    }
    #initialize(){
        for (let i = 0; i < this.columns; i++){
            this.symbols[i] = new Symbol(i, 0, this.fontSize, this.canvasHeight);
        }
    }
    resize(width, height){
        this.canvasWidth = width;
        this.canvasHeight = height;
        this.columns = this.canvasWidth/this.fontSize;
        this.symbols = [];
        this.#initialize();
    }
}

const effect = new Effect(canvas.width, canvas.height);
let lastTime = 0;
const fps = 15;
const nextFrame = 1000/fps;
let timer = 0;

function animate(timeStamp){
    const deltaTime = timeStamp - lastTime;
    lastTime = timeStamp;
    if (timer > nextFrame){
        ctx.fillStyle = 'rgba(0, 0, 0, 0.05)';
        ctx.textAlign = 'center';
        ctx.fillRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = gardient; //'#0aff0a';
        ctx.font = effect.fontSize + 'px monospace';
        effect.symbols.forEach(symbol => symbol.draw(ctx));
        timer = 0;
    } else {
        timer += deltaTime;
    }
    requestAnimationFrame(animate);
}
animate(0);

window.addEventListener('resize', function() {
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    effect.resize(canvas.width, canvas.height);
    gardient = ctx.createLinearGradient(canvas.width/2, canvas.height/2, 100, canvas.height/2, canvas.width/2);
    gardient.addColorStop(0, 'yellow');
    gardient.addColorStop(0.6, 'cyan');
    gardient.addColorStop(1, 'magenta');
});"}, "id": "151c7c40-23fb-4a85-b96c-4113a98fd178"}, {"type": "content", "value": "<p data-block-key="2bahs"></p><hr/><p data-block-key="4pclb"></p>", "id": "d350e80e-31c1-4dd1-a8e8-f0f99078b208"}, {"type": "content", "value": "<h2 data-block-key="2bahs">En résumé</h2>", "id": "5536dece-a51b-4a50-be85-c6e477b13694"}, {"type": "content", "value": "<p data-block-key="2bahs">Ce code JavaScript crée une animation dynamique de caractères tombants, typique de l'esthétique de Matrix, en utilisant un canevas HTML. Il génère des symboles aléatoires qui descendent à l'écran, en redimensionnant l'effet lors des changements de taille de la fenêtre. La logique d'animation est gérée par la méthode animate, qui utilise requestAnimationFrame pour des mises à jour continues et fluides.</p>", "id": "d0ef4937-d7dc-4ff6-87a9-43c68cb742c5"}, {"type": "content", "value": "<p data-block-key="7ge4x"></p><hr/><p data-block-key="amd1t"></p>", "id": "f8a523ba-2132-4af0-9b96-650acbca0665"}, {"type": "content", "value": "<p data-block-key="2bahs">Cliquez pour télécharger <a href="https://github.com/LaurentJouron/matrix_rain/archive/refs/heads/main.zip">le projet</a></p>", "id": "f073f2a9-b7c0-4dfb-85b2-80f367c7357e"}]

